INSERT INTO `qe_product` VALUES ('14', '2', 'Solar Powered Globe Crystal Ball String Lights Multi-color 30 LED ', '<span style=\"font-weight:700;color:#333333;font-family:Arial, sans-serif;font-size:small;background-color:#FFFFFF;\">Features:</span><br />\r\n<span style=\"color:#333333;font-family:Arial, sans-serif;font-size:small;background-color:#FFFFFF;\">Solar powered LED, no utility energy costs&nbsp;</span><br />\r\n<span style=\"color:#333333;font-family:Arial, sans-serif;font-size:small;background-color:#FFFFFF;\">Charges even on cloudy days, turns on at dusk automatically&nbsp;</span><br />\r\n<span style=\"color:#333333;font-family:Arial, sans-serif;font-size:small;background-color:#FFFFFF;\">Easy for installation and maintenance&nbsp;</span><br />\r\n<span style=\"font-weight:700;color:#333333;font-family:Arial, sans-serif;font-size:small;background-color:#FFFFFF;\">Specifications:</span><br />\r\n<span style=\"color:#333333;font-family:Arial, sans-serif;font-size:small;background-color:#FFFFFF;\">LED Color: Multi-color (R/Y/G/P)&nbsp;</span><br />\r\n<span style=\"color:#333333;font-family:Arial, sans-serif;font-size:small;background-color:#FFFFFF;\">LED Quantity: 30 LED&nbsp;</span><br />\r\n<span style=\"color:#333333;font-family:Arial, sans-serif;font-size:small;background-color:#FFFFFF;\">Rechargeable Ni-MH Battery: 1.2V 600mA&nbsp;</span><br />\r\n<span style=\"color:#333333;font-family:Arial, sans-serif;font-size:small;background-color:#FFFFFF;\">Switch: Power ON/OFF &amp; MODE&nbsp;</span><br />\r\n<span style=\"color:#333333;font-family:Arial, sans-serif;font-size:small;background-color:#FFFFFF;\">Lighting Mode: Steady ON / Flashing&nbsp;</span><br />\r\n<span style=\"color:#333333;font-family:Arial, sans-serif;font-size:small;background-color:#FFFFFF;\">Solar Panel: 2V 100mA&nbsp;</span><br />\r\n<span style=\"color:#333333;font-family:Arial, sans-serif;font-size:small;background-color:#FFFFFF;\">Charging Time: 6 hours&nbsp;</span><br />\r\n<span style=\"color:#333333;font-family:Arial, sans-serif;font-size:small;background-color:#FFFFFF;\">Light Length: 19.7ft (6m), 5ft lead cable included&nbsp;</span><br />\r\n<span style=\"color:#333333;font-family:Arial, sans-serif;font-size:small;background-color:#FFFFFF;\">Length between 2 bulbs: 0.5ft (0.15m)&nbsp;</span><br />\r\n<span style=\"color:#333333;font-family:Arial, sans-serif;font-size:small;background-color:#FFFFFF;\">Length from Solar panel to first stake: 5ft (1.5m)&nbsp;</span><br />\r\n<span style=\"color:#333333;font-family:Arial, sans-serif;font-size:small;background-color:#FFFFFF;\">Solar panel with ground stake for easy positioning&nbsp;</span><br />\r\n<span style=\"font-weight:700;color:#333333;font-family:Arial, sans-serif;font-size:small;background-color:#FFFFFF;\">Warm Tips:&nbsp;</span><span style=\"color:#333333;font-family:Arial, sans-serif;font-size:small;background-color:#FFFFFF;\">Solar Light will have best performance, when you place it in a sunny area or an area with direct sun most of the day time.&nbsp;</span><br />\r\n<span style=\"color:#333333;font-family:Arial, sans-serif;font-size:small;background-color:#FFFFFF;\">To get better performance, we recommend 100% charged before your fist use.&nbsp;</span><br />\r\n<span style=\"font-weight:700;color:#333333;font-family:Arial, sans-serif;font-size:small;background-color:#FFFFFF;\">What\'s in the box:</span><br />\r\n<span style=\"color:#333333;font-family:Arial, sans-serif;font-size:small;background-color:#FFFFFF;\">1 x Qedertek Globe String Lights&nbsp;</span><br />\r\n<span style=\"color:#333333;font-family:Arial, sans-serif;font-size:small;background-color:#FFFFFF;\">1 x Solar Panel with Ground Stake&nbsp;</span><br />\r\n<span style=\"color:#333333;font-family:Arial, sans-serif;font-size:small;background-color:#FFFFFF;\">1 x User Manual</span>', '<ul class=\"a-unordered-list a-vertical a-spacing-none\" style=\"color:#949494;font-family:Arial, sans-serif;font-size:13.3333px;back